SELECT 

FROM sys_party_event a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.partyid = #{partyid}


		AND a.eventid = #{eventid}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_party_event a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
